/**
 * Replace all backslashes with forward slashes
 * @category String
 */
export declare const slash: (s: string) => string;
/**
 * First letter uppercase, other lowercase
 * @category String
 * @example
 * ```ts
 * capitalize('hello') // 'Hello'
 * ```
 */
export declare function capitalize(s: string): string;
export declare function ensurePrefix(prefix: string, str: string): string;
export declare function ensureSuffix(suffix: string, str: string): string;
/**
 * @category String
 * @example
 * ```ts
 * kebabCase('a b c') // => a-b-c
 * kebabCase('orderBy') // => order-by
 * ```
 */
export declare function kebabCase(str: string): string;
/**
 * @category string
 * @example
 * ```ts
 * camelCase('foo bar') // => fooBar
 * camelCase('foo-bar') // => fooBar
 * ```
 */
export declare function camelCase(str: string): string;
